package project.spring.securitystudent.controller;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import project.spring.securitystudent.entity.Student;import java.util.Arrays;import java.util.List;@RestController@RequestMapping("/api/students")public class StudentController {    private static final List<Student> STUDENTS = Arrays.asList(            new Student(1, "nicolas"),            new Student(2, "dyana"),            new Student(3, "maria")    );    @GetMapping("/{id}")    public Student getStudent(@PathVariable Integer id) {        return STUDENTS.stream()                .filter(student -> id.equals(student.getId()))                .findFirst()                .orElseThrow(() -> new IllegalStateException("student " + id + " does not exists"));    }}